'',
'--Sekwencje i triggery',
'',
'DROP SEQUENCE seq_pomieszczenia;',
'CREATE SEQUENCE seq_pomieszczenia START WITH 10 INCREMENT BY 10;',
'',
'CREATE OR REPLACE TRIGGER trg_pomieszczenia_pk',
'BEFORE INSERT ON pomieszczenia',
'FOR EACH ROW',
'BEGIN',
