'    v_zabiegi NUMBER;',
'    v_szczepienia NUMBER;',
'BEGIN',
'    SELECT COUNT(*) INTO v_zabiegi',
'    FROM zabiegi',
unistr('    WHERE id_zwierz = p_id_zwierz AND status = ''N''; -- N = niezako\0144czony'),
